import React from "react";
import "SuggestionCard.css";

const Suggestion_card = ({ advice }) => {
    return (
        <div className="suggestion_card">
            <div className="suggestion_card_title">{advice.txt}</div>
            <p className="suggestion_card_description">{advice.description}</p>
        </div>
    );
}

export default Suggestion_card;
