// Create a react component that inputs a textarea message then performs a fetch request to localhost:3001 and get back a response as a data.message and displays the message in a box below


// should be a resume and job description

import React, { Component } from 'react';
import './App.css';

function App(){
  const [message, setMessage] = React.useState(''); // message to send to server
  const [response, setResponse] = React.useState(''); // response from server

  const handleChange = (event) => {
    setMessage(event.target.value);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    fetch('http://localhost:3001', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({message: message}),
    })
    .then(response => response.json())
    .then(data => {
      setResponse(data.message);
    });
  }

  // the textarea box and response text should be in the middle of the left screen and the right screen
  return (
  <div className="App">
  <div className="App_Header">
    <h1>Resume Advisor v1.0</h1>
  </div>
  <div className="User_Submit">
    <form onSubmit={handleSubmit}>
      <h1>Resume Advicer</h1>
      <p>Put Your Resume Text Below</p>
      <div>
        <label>
          <textarea className='textbox' placeholder='Put our resume text, should start with your name' value={message} onChange={handleChange} />
        </label>
      </div>

      <input type="submit" value="Submit" />
    </form>
  </div>

  <div className="Model_Response">
    <h1>Here is the top advice!</h1>
    <p>{response}</p>
    <></>
  </div>

  </div>
);
}

export default App;
